mapper.select:
SELECT COUNT(1) FROM TP_BOARD WHERE DEL_FG=0
AND CATEGORY_NO = #{categoryNo}
AND BOARD_TITLE LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE TP_BOARD

		CATEGORY_NO = #{categoryNo}
		,BOARD_TITLE = #{boardTitle}
		,BOARD_CONTENT = #{boardContent}
		,MOD_ID = #{userNo}
		,MOD_DATE = SYSDATE


		AND DEL_FG = 0
		AND BOARD_NO = #{boardNo}

UPDATE TP_BOARD

		DEL_FG = 1


		AND BOARD_NO = #{boardNo}
		AND USER_NO = #{userNo}
