mapper.select:
SELECT COUNT(1) FROM TP_BOARD WHERE DEL_FG=0
AND CATEGORY_NO = #{category}
AND BOARD_TITLE LIKE '%'||#{keyword}||'%'

mapper.update:
UPDATE TP_BOARD

		CATEGORY_NO = #{categoryNo}
		,BOARD_TITLE = #{boardTitle}
		,BOARD_CONTENT = #{boardContent}
		,MOD_ID = #{userNo}
		,MOD_DATE = SYSDATE


		AND DEL_FG = 0
		AND BOARD_NO = #{boardNo}

UPDATE TP_BOARD

		DEL_FG = 1


		AND BOARD_NO = #{boardNo}
		AND USER_NO = #{userNo}

mapper.insert:
		INSERT INTO TP_BOARD(
			BOARD_NO
			,CATEGORY_NO
			,USER_NO
			,BOARD_TITLE
			,BOARD_CONTENT
			,REG_ID
			,REG_DATE
			,MOD_DATE
			,MOD_ID
			,DEL_FG
		)VALUES(
			BOARD_SEQ.NEXTVAL
			,#{categoryNo}
			,#{userNo}
			,#{boardTitle}
			,#{boardContent}
			,#{userNo}
			,SYSDATE
			,SYSDATE
			,#{userNo}
			,'0'
		)

       		SELECT BOARD_SEQ.CURRVAL FROM DUAL
