mapper.select:
SELECT USER_NO ,USER_ID ,USER_PW ,USER_NAME ,USER_EMAIL ,USER_PHONE ,USER_GENDER ,USER_RANK ,USER_START_DATE ,USER_CHECK FROM TP_USER
AND USER_CHECK = 0
AND USER_ID = #{userId}
AND USER_NO = #{userNo}
AND USER_EMAIL = #{userEmail}

SELECT COUNT(1)
	FROM TP_USER

		AND USER_CHECK = 0

			AND USER_NO = #{userNo}


			AND USER_ID = #{userId}


			AND USER_NAME = #{userName}


			AND USER_EMAIL = #{userEmail}

mapper.update:
UPDATE TP_USER


		,USER_NAME = #{userName}


		,USER_EMAIL = #{userEmail}




		USER_NO = (SELECT USER_NO FROM TP_USER WHERE USER_ID = #{userId})

	OR USER_NO = #{userNo}

UPDATE TP_USER

		USER_PW = #{userPw}


		AND USER_NO = (SELECT USER_NO FROM TP_USER WHERE USER_ID = #{userId})
		AND USER_ID = #{userId}
