mapper.select:
SELECT COUNT(SERIAL)
FROM
TP_EMAIL_SERIAL

	AND DE_FLG = 0

		AND SERIAL_NO = #{serialNo}


		AND SERIAL = #{serial}


		AND USER_NO = (SELECT USER_NO FROM TP_USER WHERE USER_ID = #{userId})


		AND USER_EMAIL = #{userEmail}

mapper.update:
UPDATE TP_EMAIL_SERIAL

		DE_FLG = 1



			AND SERIAL_NO = #{serialNo}


			AND USER_NO = (SELECT USER_NO FROM TP_USER WHERE USER_ID = #{userId})


			AND USER_EMAIL = #{userEmail}
